public class ifStatements {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 99 ;

		if ( age <= 10 ){ System.out.print("\n\tYou are in between 0 and 10 years old.");
			}
		else if ( age <= 20 ){ System.out.print("\n\tYou are in between 10 and 20 years old.");
			}
		else if ( age <= 30 ){ System.out.print("\n\tYou are in between 20 and 30 years old.");
			}
		else if ( age <= 40 ){ System.out.print("\n\tYou are in between 30 and 40 years old.");
			}
		else if ( age <= 50 ){ System.out.print("\n\tYou are in between 40 and 50 years old.");
			}
		else if ( age <= 60 ){ System.out.print("\n\tYou are in between 50 and 60 years old.");
			}
		else if ( age <= 70 ){ System.out.print("\n\tYou are in between 60 and 70 years old.");
			}
		else if ( age <= 80 ){ System.out.print("\n\tYou are in between 70 and 80 years old.");
			}
		else if ( age <= 90 ){ System.out.print("\n\tYou are in between 80 and 90 years old.");
			}
		else if ( age <= 100 ){ System.out.print("\n\tYou are in between 90 and 100 years old.");
		}
		else { System.out.print("\n\tYou are older than 100 years old."); }
	}

}
